<?php/* * Template: Simple */?><script type="text/html" id='relatify_output'>    <div  class="full_box" >        <div class="related-content-title" >            <%= items.title %> <%= items.width %>        </div>        <div class="content-list-wrapper">            <% _.each(items.posts,function(item,key,list){ %>            <div class="single_relatified_item">                <a href="<%= item.permalink %>">                    <% if(item.image != "") { %>                    <img src="<%= item.image %>" style="<% if(item.width != ""){ %>width:<%= item.width %>px; <% } %><% if(item.height != ""){ %>height:<%= item.height %>px;<% } %>" />                         <% } %>                         <div class="single_relatified_post_title">                        <p><%= item.post_title %></p>                    </div>                </a>            </div>            <% }); %>        </div>        <% if( items.love == 1 ) { %>        <div class="relatify_love">            <p>Related contents by <a href="<%= items.love_url %>" target="_blank">Relatify</a></p>        </div>        <% } %>    </div></script><!-- Create your target --><div id="relatify_output_target"></div><style>    .single_relatified_item {        float: left;        width: 25%;    }    .single_relatified_item img {        border: 2px solid #d7d7d7;        width: 100%;    }    .single_relatified_item:first-child {        margin-left: 0px !important;    }    .single_relatified_post_title {        -moz-border-bottom-colors: none;        -moz-border-left-colors: none;        -moz-border-right-colors: none;        -moz-border-top-colors: none;        background-color: #3d3d3d;        border-color: -moz-use-text-color #d7d7d7 #d7d7d7;        border-image: none;        border-right: 2px solid #d7d7d7;        border-style: none solid solid;        border-width: 0 2px 2px;        height: 38px;        padding: 3px;        position: relative;    }    .full_box {    }    .single_relatified_post_title p {        color: #fff;        font-size: 12px !important;        height: 30px;        line-height: 1.2;        margin-bottom: 0 !important;        overflow: hidden;        padding-left: 2px;    }    .related-content-title {        margin-bottom: 10px;    }</style>